// Configurable Variables
const NUM_READINGS = 20; // Number of readings to start and average
const TRIM = 10; // Percent of readings from top and bottom to trim before averaging
const MEMORY_SIZE = 10; // Number of readings to keep in memory (rolling window)
const NUM_TRIGGERS = 3; // Number of readings over or under the thresholds needed to turn tool on
const ERROR_THRESHOLD = 100; // Number of null readings to trigger "off" state
const boardName = "ADS1115 on I2C-1, Addr 0x48"; // Board name to track tools

// Initialize tools from global config
const config = global.get('current_sensors') || [];
const tools = config.filter(tool => tool.address.board === boardName);

// Initialize tool tracking structures
let toolsStatus = {};
tools.forEach(tool => {
    toolsStatus[tool.id] = {
        rollingWindows: {
            [`${tool.address.channel}_readings`]: [] // Create a rolling window for the specific channel
        },
        errorCount: 0,
        toolStatus: false
    };
});

// Helper function to calculate average, trimming top/bottom readings
function calculateAverage(readings) {
    const sorted = readings.sort((a, b) => a - b);
    const trimCount = Math.floor(readings.length * TRIM / 100);
    const trimmed = sorted.slice(trimCount, sorted.length - trimCount);
    const sum = trimmed.reduce((acc, val) => acc + val, 0);
    return sum / trimmed.length;
}

// Helper function to process tool readings
function processToolReadings(toolId, voltageReading) {
    const tool = tools.find(t => t.id === toolId);
    if (!tool) return;

    const toolStatus = toolsStatus[toolId];
    const channelKey = `${tool.address.channel}_readings`; // Access rolling window by channel

    // Print out the current voltage reading for debugging
    console.log(`Reading voltage for ${tool.label} (Channel: ${tool.address.channel}) - Voltage: ${voltageReading}`);

    // Update rolling window for the channel
    toolStatus.rollingWindows[channelKey].push(voltageReading);

    // Ensure we only keep MEMORY_SIZE readings
    if (toolStatus.rollingWindows[channelKey].length > MEMORY_SIZE) {
        toolStatus.rollingWindows[channelKey].shift();
    }

    // Print out the current rolling window for debugging
    console.log(`${tool.label} (Channel: ${tool.address.channel}) - Current Rolling Window: ${toolStatus.rollingWindows[channelKey]}`);

    // Calculate the average of the window
    const average = calculateAverage(toolStatus.rollingWindows[channelKey]);
    const max = average * (1 + tool.preferences.threshold);
    const min = average * (1 - tool.preferences.threshold);

    // Debugging: print average, min, and max
    console.log(`${tool.label} (Channel: ${tool.address.channel}) - Average: ${average}, Min: ${min}, Max: ${max}`);

    // Check if current reading is within the min/max range
    const isReadingValid = voltageReading >= min && voltageReading <= max;

    // Update tool status based on NUM_TRIGGERS
    let triggers = 0;
    toolStatus.rollingWindows[channelKey].forEach(reading => {
        if (reading >= max || reading <= min) {
            triggers++;
        }
    });

    if (triggers >= NUM_TRIGGERS) {
        toolStatus.toolStatus = true;
    } else {
        toolStatus.toolStatus = false;
    }

    // Error handling for null readings
    if (voltageReading === null) {
        toolStatus.errorCount++;
        if (toolStatus.errorCount >= ERROR_THRESHOLD) {
            toolStatus.toolStatus = false; // Turn tool off after ERROR_THRESHOLD nulls
            console.log(`${tool.label} (Channel: ${tool.address.channel}) - Tool is OFF due to errors.`);
        }
    } else {
        toolStatus.errorCount = 0; // Reset error count if valid reading
    }

    // Send tool status update (This is where you'd trigger on/off actions)
    if (toolStatus.toolStatus) {
        console.log(`${tool.label} is ON (Channel: ${tool.address.channel})`);
    } else {
        console.log(`${tool.label} is OFF (Channel: ${tool.address.channel})`);
    }
}

// Function to handle incoming ADS1115 messages
function handleADS1115Message(msg) {
    // Extract the data from the message
    const data = msg.payload["/dev/i2c-1"]["ads1115"]["0x48"]["singleEnded"];

    // Iterate over all tools and process their respective channel reading
    tools.forEach(tool => {
        console.log(`Tool: ${JSON.stringify(tool)}`); // Debug entire tool object
        const channel = tool.address.channel; // Get channel from tool address

        console.log(`Using Channel: ${channel}`); // Debug the channel value

        // Check if the channel exists in the incoming data
        if (!data[channel]) {
            console.log(`Channel ${channel} not found in incoming data!`);
            return; // Skip if the channel is missing
        }

        // Retrieve the voltage reading for the specific channel
        const voltageReading = data[channel]?.Volts || null; // Retrieve voltage reading for the specific channel

        // Debug: print the voltage reading
        console.log(`Voltage Reading for ${tool.label} (Channel: ${channel}): ${voltageReading}`);

        // Process the voltage reading for this tool
        processToolReadings(tool.id, voltageReading);
    });
}




