const ADS_TYPE = "ads1115"; // Set to "ads1015" if needed
const BOARD_ADDRESS = "0x48"; // Update with the board address
const CONTEXT_NAME = `voltReadings_${BOARD_ADDRESS}`; // Unique context name
const INITIALIZED_FLAG = `initialized_${BOARD_ADDRESS}`; // Flag to track initialization

// Fetch global constants
const constants = global.get("current_sensor_constants");

// Use constants
const NUM_READINGS = constants?.NUM_READINGS || 20;

// Retrieve or initialize the rolling window for channel readings
let channelReadings = flow.get(CONTEXT_NAME) || {
    "channel_0": [],
    "channel_1": [],
    "channel_2": [],
    "channel_3": []
};

// Debugging: Inspect incoming payload
console.log("Incoming Payload:", msg.payload);

// Safely extract data from the incoming message
const data = msg.payload?.["/dev/i2c-1"]?.[ADS_TYPE]?.[BOARD_ADDRESS]?.["singleEnded"];
if (!data) {
    console.error(`Data not found for ${BOARD_ADDRESS}. Payload might not have the expected structure.`);
    return null; // Exit the function if data is missing
}

// Process each channel
for (let channel in data) {
    const voltage = data[channel].Volts;

    // Maintain a rolling window of NUM_READINGS
    if (channelReadings[channel].length >= NUM_READINGS) {
        channelReadings[channel].shift(); // Remove the oldest reading
    }
    channelReadings[channel].push(voltage);
}

// Store updated readings in flow context
flow.set(CONTEXT_NAME, channelReadings);

// Check if all channels have reached NUM_READINGS
const isInitialized = Object.values(channelReadings).every(arr => arr.length >= NUM_READINGS);

// Check if the initialization message has already been sent
