if (isInitialized && !alreadyInitialized) {
    // Log initialization status
    console.log(`Voltage readings for ${BOARD_ADDRESS} initialized.`);

    // Set the initialization flag
    flow.set(INITIALIZED_FLAG, true);

    // Send initialization message
