// Track active tools per collector
let activeCollectors = {};

// Process tools to determine which collectors are needed
tools.forEach(tool => {
    if (tool.state) { // If the tool is on
        (tool.preferences.collector_prefs || []).forEach(pref => {
            if (!activeCollectors[pref.name]) {
                activeCollectors[pref.name] = { spin_down_time: pref.spin_down_time };
            } else {
                // Update spin_down_time to the longer value
                activeCollectors[pref.name].spin_down_time = Math.max(
                    activeCollectors[pref.name].spin_down_time,
                    pref.spin_down_time
                );
            }
        });
    }
});

// Process each collector
collectors.forEach(collector => {
    let collectorId = collector.id;

    // Initialize collector state if not already present
    if (!collectorState[collectorId]) {
        collectorState[collectorId] = {
            active: false,
            spinDownTimer: null
        };
    }

    let state = collectorState[collectorId];

    if (activeCollectors[collectorId]) {
        // Collector is needed
        if (!state.active) {
            state.active = true;
            // Turn on the collector
            node.send({ topic: collectorId, payload: true });
        }

        // Update spin_down_time in memory
        state.spin_down_time = activeCollectors[collectorId].spin_down_time;

        // Cancel any existing spin down timer
        if (state.spinDownTimer) {
            clearTimeout(state.spinDownTimer);
            state.spinDownTimer = null;
        }
    } else {
        // Collector is not needed, check if it should be turned off
        if (state.active && !state.spinDownTimer) {
            state.spinDownTimer = setTimeout(() => {
                state.active = false;
                state.spinDownTimer = null;
                // Turn off the collector
                node.send({ topic: collectorId, payload: false });
            }, state.spin_down_time * 1000);
        }
    }
});

// Save the updated collector state
