// Cache the global lists of lights and light styles
let lights = global.get("lights") || [];
let lightStyles = global.get("light_styles") || [];

// Ensure lightStyles is an array
if (!Array.isArray(lightStyles)) {
    node.warn("lightStyles is not an array. Check global.set('light_styles').");
    lightStyles = [];
}

// Get the tool states from the shop manager's output
let tools = msg.payload || [];

// Create a list of lights that need to be turned on
let onLights = [];

// Build the list of lights that are requested to be "on"
tools.forEach(tool => {
    if (tool.state) {
        (tool.preferences.light_prefs || []).forEach(lightId => {
            if (!onLights.includes(lightId)) {
                onLights.push(lightId);
            }
        });
    }
});

// Process each light in the global lights list
lights.forEach(light => {
    let lightState = onLights.includes(light.id) ? "true" : "false";

    // Resolve styles for the light
    let styles = Object.keys(light.styles || {}).length
        ? light.styles // Use styles from the light itself if they exist
        : lightStyles.find(style => style.type === light.type)?.styles || {}; // Fallback to global light_styles

    if (!Object.keys(styles).length) {
        // Log if styles are not found at all
        node.warn(`No styles found for light ID: ${light.id}, Type: "${light.type}"`);
        return; // Skip this light
    }

    if (!styles[lightState]) {
        // Log if the specific state is not found
        node.warn(`No styles found for light ID: ${light.id} in state: ${lightState}`);
        return; // Skip this light
    }

    // Handle different light types
    if (light.type === "pwm_rgbled_button") {
        ["red", "green", "blue"].forEach(color => {
            let value = styles[lightState][color];
            if (value !== undefined) {
                node.send({
                    topic: `${light.id}_${color}`,
                    payload: value
                });
            } else {
                node.warn(`Missing color value: ${color} for light ID: ${light.id}`);
            }
        });
    } else if (light.type === "pwm_led_button") {
        let value = styles[lightState].value;
        if (value !== undefined) {
            node.send({
                topic: light.id,
                payload: value
            });
        } else {
            node.warn(`Missing value for pwm_led_button light ID: ${light.id}`);
        }
    } else if (light.type === "relay") {
        let value = styles[lightState].value;
        if (value !== undefined) {
            node.send({
                topic: light.id,
                payload: !!value // Ensure it's a boolean
            });
        } else {
            node.warn(`Missing value for relay light ID: ${light.id}`);
        }
    }
});

// Pulse parameters
const maxBrightness = 60; // LED "off" brightness
const minBrightness = 0; // LED "on" brightness
const pulseDuration = 500; // milliseconds for one full pulse
const now = new Date().getTime();

// Calculate the phase of the wave
const phase = (now % pulseDuration) / pulseDuration;
const sineValue = Math.sin(phase * 2 * Math.PI); // Sine wave

// Map sineValue (-1 to 1) to brightness range
const brightness = Math.round(
    ((sineValue + 1) / 2) * (maxBrightness - minBrightness) + minBrightness
);

// Output brightness value
msg.payload = brightness;
msg.topic = "miter_saw_right_button_LED";
return msg;

